INSERT INTO quotes (text, speaker, date, thumbnail, social_link, role, source, source_detail, historical_context)
VALUES ('You have power over your mind — not outside events. Realize this, and you will find strength.', 'Marcus Aurelius', 'c. 170 AD', 'https://ui-avatars.com/api/?name=Marcus+Aurelius&background=CC785C&color=fff&size=80&rounded=true', 'https://en.wikipedia.org/wiki/Marcus_Aurelius', 'Roman Emperor and Stoic philosopher', 'Book', 'Meditations, Book 6', 'Written during the Antonine Plague and Germanic wars, these private reflections show a leader choosing inner discipline over despair — a timeless model for leading through crisis.');

INSERT INTO quotes (text, speaker, date, thumbnail, social_link, role, source, source_detail, historical_context)
VALUES ('The unexamined life is not worth living.', 'Socrates', '399 BC', 'https://ui-avatars.com/api/?name=Socrates&background=8B5E3C&color=fff&size=80&rounded=true', 'https://en.wikipedia.org/wiki/Socrates', 'Athenian philosopher', 'Trial transcript', 'Plato''s Apology — Socrates'' defense at his trial', 'Spoken at his own trial where he faced execution, Socrates chose death over abandoning philosophy. The original declaration that self-reflection is not optional — it is the point.');

INSERT INTO quotes (text, speaker, date, thumbnail, social_link, role, source, source_detail, historical_context)
VALUES ('We suffer more often in imagination than in reality.', 'Seneca', 'c. 65 AD', 'https://ui-avatars.com/api/?name=Seneca&background=D4956B&color=fff&size=80&rounded=true', 'https://en.wikipedia.org/wiki/Seneca_the_Younger', 'Roman Stoic philosopher, statesman, and advisor to Nero', 'Letter', 'Letters to Lucilius (Moral Letters), Letter 13', 'Written to his friend Lucilius, this insight cuts through the anxiety that defines every era. In an age of information overload and catastrophizing, Seneca''s antidote is as potent as ever.');

INSERT INTO quotes (text, speaker, date, thumbnail, social_link, role, source, source_detail, historical_context)
VALUES ('It''s not what happens to you, but how you react to it that matters.', 'Epictetus', 'c. 108 AD', 'https://ui-avatars.com/api/?name=Epictetus&background=C49A6C&color=fff&size=80&rounded=true', 'https://en.wikipedia.org/wiki/Epictetus', 'Greek Stoic philosopher, born a slave', 'Teaching', 'The Discourses, recorded by his student Arrian', 'Born into slavery, Epictetus taught that true freedom is internal. His philosophy formed the bedrock of cognitive behavioral therapy two millennia later.');

INSERT INTO quotes (text, speaker, date, thumbnail, social_link, role, source, source_detail, historical_context)
VALUES ('We are what we repeatedly do. Excellence, then, is not an act, but a habit.', 'Will Durant', '1926', 'https://ui-avatars.com/api/?name=Will+Durant&background=A67C5B&color=fff&size=80&rounded=true', 'https://en.wikipedia.org/wiki/Will_Durant', 'Historian and philosopher, summarizing Aristotle', 'Book', 'The Story of Philosophy (1926), paraphrasing Aristotle''s Nicomachean Ethics', 'Often attributed to Aristotle himself, this elegant distillation by Durant captures the core Aristotelian insight: character is built through practice, not through moments of inspiration.');

INSERT INTO quotes (text, speaker, date, thumbnail, social_link, role, source, source_detail, historical_context)
VALUES ('The truth is like a lion; you don''t have to defend it. Let it loose; it will defend itself.', 'St. Augustine', 'c. 400 AD', 'https://ui-avatars.com/api/?name=St+Augustine&background=CC785C&color=fff&size=80&rounded=true', 'https://en.wikipedia.org/wiki/Augustine_of_Hippo', 'Bishop of Hippo, theologian, and Doctor of the Church', 'Attributed', 'Widely attributed to Augustine, likely a paraphrase of his writings on truth', 'In an era of competing narratives and information warfare, Augustine''s confidence in truth''s inherent power offers a radical alternative to anxious defensiveness.');

INSERT INTO quotes (text, speaker, date, thumbnail, social_link, role, source, source_detail, historical_context)
VALUES ('Silence in the face of evil is itself evil. God will not hold us guiltless. Not to speak is to speak. Not to act is to act.', 'Dietrich Bonhoeffer', 'c. 1940', 'https://ui-avatars.com/api/?name=Dietrich+Bonhoeffer&background=8B5E3C&color=fff&size=80&rounded=true', 'https://en.wikipedia.org/wiki/Dietrich_Bonhoeffer', 'German Lutheran pastor and anti-Nazi dissident', 'Attributed', 'Widely attributed to Bonhoeffer during his resistance against the Nazi regime', 'Bonhoeffer was executed in a concentration camp weeks before liberation. His life and words remain the ultimate rebuttal to comfortable neutrality in the face of injustice.');

