INSERT INTO quotes (text, speaker, date, thumbnail, social_link, role, source, source_detail, historical_context)
VALUES ('Courage is not simply one of the virtues, but the form of every virtue at the testing point.', 'C.S. Lewis', '1943', 'https://ui-avatars.com/api/?name=CS+Lewis&background=D4956B&color=fff&size=80&rounded=true', 'https://en.wikipedia.org/wiki/C._S._Lewis', 'Author, literary scholar, and Christian apologist', 'Book', 'The Screwtape Letters (1942) — a senior demon advising a junior one', 'Written during the London Blitz, Lewis understood that every virtue — honesty, kindness, faith — requires courage to practice when it costs you something.');

INSERT INTO quotes (text, speaker, date, thumbnail, social_link, role, source, source_detail, historical_context)
VALUES ('He who has a why to live can bear almost any how.', 'Friedrich Nietzsche', '1889', 'https://ui-avatars.com/api/?name=Friedrich+Nietzsche&background=C49A6C&color=fff&size=80&rounded=true', 'https://en.wikipedia.org/wiki/Friedrich_Nietzsche', 'German philosopher', 'Book', 'Twilight of the Idols (1889), Maxims and Arrows, #12', 'Later made famous by Viktor Frankl''s Man''s Search for Meaning, this maxim bridges philosophy and survival psychology — purpose is the ultimate anchor.');

INSERT INTO quotes (text, speaker, date, thumbnail, social_link, role, source, source_detail, historical_context)
VALUES ('The line separating good and evil passes not through states, nor between classes, nor between political parties — but right through every human heart.', 'Aleksandr Solzhenitsyn', '1973', 'https://ui-avatars.com/api/?name=Aleksandr+Solzhenitsyn&background=A67C5B&color=fff&size=80&rounded=true', 'https://en.wikipedia.org/wiki/Aleksandr_Solzhenitsyn', 'Russian novelist, Nobel laureate, and Soviet dissident', 'Book', 'The Gulag Archipelago (1973), Part 1, Chapter 4', 'After eight years in Soviet labor camps, Solzhenitsyn''s refusal to demonize ''the other side'' remains one of the most penetrating moral observations of the 20th century.');

INSERT INTO quotes (text, speaker, date, thumbnail, social_link, role, source, source_detail, historical_context)
VALUES ('It is not the critic who counts; not the man who points out how the strong man stumbles. The credit belongs to the man who is actually in the arena.', 'Theodore Roosevelt', 'April 23, 1910', 'https://ui-avatars.com/api/?name=Theodore+Roosevelt&background=CC785C&color=fff&size=80&rounded=true', 'https://en.wikipedia.org/wiki/Theodore_Roosevelt', '26th President of the United States', 'Speech', '"Citizenship in a Republic" — speech at the Sorbonne, Paris', 'Delivered after leaving office, Roosevelt''s ''Man in the Arena'' speech has become the definitive answer to armchair critics everywhere — and the unofficial motto of every founder, builder, and risk-taker.');

INSERT INTO quotes (text, speaker, date, thumbnail, social_link, role, source, source_detail, historical_context)
VALUES ('The only thing we have to fear is fear itself — nameless, unreasoning, unjustified terror which paralyzes needed efforts to convert retreat into advance.', 'Franklin D. Roosevelt', 'March 4, 1933', 'https://ui-avatars.com/api/?name=Franklin+Roosevelt&background=8B5E3C&color=fff&size=80&rounded=true', 'https://en.wikipedia.org/wiki/Franklin_D._Roosevelt', '32nd President of the United States', 'Speech', 'First Inaugural Address', 'Spoken at the depths of the Great Depression with 25% unemployment, FDR''s opening line reframed a national crisis from hopeless to conquerable — and a country believed him.');

INSERT INTO quotes (text, speaker, date, thumbnail, social_link, role, source, source_detail, historical_context)
VALUES ('Darkness cannot drive out darkness; only light can do that. Hate cannot drive out hate; only love can do that.', 'Martin Luther King Jr.', '1963', 'https://ui-avatars.com/api/?name=Martin+Luther+King&background=D4956B&color=fff&size=80&rounded=true', 'https://en.wikipedia.org/wiki/Martin_Luther_King_Jr.', 'Baptist minister, civil rights leader', 'Book', 'Strength to Love (1963), Chapter 5', 'Written in the crucible of the civil rights movement, King''s insistence on love over hatred was not naivety — it was strategic, theological, and ultimately world-changing.');

INSERT INTO quotes (text, speaker, date, thumbnail, social_link, role, source, source_detail, historical_context)
VALUES ('If you''re going through hell, keep going.', 'Winston Churchill', 'c. 1940s', 'https://ui-avatars.com/api/?name=Winston+Churchill&background=C49A6C&color=fff&size=80&rounded=true', 'https://en.wikipedia.org/wiki/Winston_Churchill', 'Prime Minister of the United Kingdom', 'Attributed', 'Widely attributed to Churchill during World War II', 'Whether Churchill actually said this or not, the attribution stuck because it perfectly captures his bulldog spirit during Britain''s darkest hours — when stopping meant losing everything.');

