INSERT INTO quotes (text, speaker, date, thumbnail, social_link, role, source, source_detail, historical_context)
VALUES ('A ship in harbor is safe, but that is not what ships are built for.', 'John A. Shedd', '1928', 'https://ui-avatars.com/api/?name=John+Shedd&background=A67C5B&color=fff&size=80&rounded=true', 'https://en.wikipedia.org/wiki/John_A._Shedd', 'American author and professor', 'Book', 'Salt from My Attic (1928)', 'A perfect metaphor for the tension between safety and purpose. In an era of risk aversion and comfort optimization, Shedd''s simple image asks: what are you built for?');

INSERT INTO quotes (text, speaker, date, thumbnail, social_link, role, source, source_detail, historical_context)
VALUES ('Not all those who wander are lost.', 'J.R.R. Tolkien', '1954', 'https://ui-avatars.com/api/?name=JRR+Tolkien&background=CC785C&color=fff&size=80&rounded=true', 'https://en.wikipedia.org/wiki/J._R._R._Tolkien', 'Author, philologist, and Oxford professor', 'Novel', 'The Fellowship of the Ring — from the poem ''All That is Gold Does Not Glitter''', 'Tolkien wrote about Aragorn, but the line resonates with anyone whose path doesn''t look conventional. A quiet defense of the unconventional journey.');

INSERT INTO quotes (text, speaker, date, thumbnail, social_link, role, source, source_detail, historical_context)
VALUES ('Every saint has a past, and every sinner has a future.', 'Oscar Wilde', '1893', 'https://ui-avatars.com/api/?name=Oscar+Wilde&background=8B5E3C&color=fff&size=80&rounded=true', 'https://en.wikipedia.org/wiki/Oscar_Wilde', 'Irish poet, playwright, and wit', 'Play', 'A Woman of No Importance (1893), Act III', 'Wilde — who would himself be imprisoned two years later — offered a grace-filled view of human imperfection that cuts against every culture''s impulse to sort people into permanent categories.');

INSERT INTO quotes (text, speaker, date, thumbnail, social_link, role, source, source_detail, historical_context)
VALUES ('The best time to plant a tree was twenty years ago. The second best time is now.', 'Chinese Proverb', 'Traditional', 'https://ui-avatars.com/api/?name=Chinese+Proverb&background=D4956B&color=fff&size=80&rounded=true', 'https://en.wikipedia.org/wiki/Chinese_proverbs', 'Traditional wisdom', 'Proverb', 'Ancient Chinese proverb, origin unknown', 'The most effective antidote to regret ever distilled into two sentences. It acknowledges the loss and immediately redirects toward agency.');

INSERT INTO quotes (text, speaker, date, thumbnail, social_link, role, source, source_detail, historical_context)
VALUES ('Do not go gentle into that good night. Rage, rage against the dying of the light.', 'Dylan Thomas', '1951', 'https://ui-avatars.com/api/?name=Dylan+Thomas&background=C49A6C&color=fff&size=80&rounded=true', 'https://en.wikipedia.org/wiki/Dylan_Thomas', 'Welsh poet', 'Poem', '"Do not go gentle into that good night" — written as his father was dying', 'A son''s plea to his dying father became an immortal hymn to defiance. Thomas insists that surrender — to death, to mediocrity, to complacency — should never come quietly.');

INSERT INTO quotes (text, speaker, date, thumbnail, social_link, role, source, source_detail, historical_context)
VALUES ('One person with courage is a majority.', 'Thomas Jefferson', 'c. 1800', 'https://ui-avatars.com/api/?name=Thomas+Jefferson&background=A67C5B&color=fff&size=80&rounded=true', 'https://en.wikipedia.org/wiki/Thomas_Jefferson', '3rd President of the United States, author of the Declaration of Independence', 'Attributed', 'Widely attributed to Jefferson in various forms', 'From a man who staked his life on a revolutionary idea, this captures the asymmetric power of conviction. History is not shaped by consensus, but by those willing to stand before one forms.');

INSERT INTO quotes (text, speaker, date, thumbnail, social_link, role, source, source_detail, historical_context)
VALUES ('The happiness of your life depends upon the quality of your thoughts.', 'Marcus Aurelius', 'c. 170 AD', 'https://ui-avatars.com/api/?name=Marcus+Aurelius&background=CC785C&color=fff&size=80&rounded=true', 'https://en.wikipedia.org/wiki/Marcus_Aurelius', 'Roman Emperor and Stoic philosopher', 'Book', 'Meditations, Book 5', 'The most powerful man in the world journaling about mental discipline. Marcus didn''t have a therapist — he had philosophy, and he prescribed himself cognitive restructuring 1,800 years early.');

