INSERT INTO quotes (text, speaker, date, thumbnail, social_link, role, source, source_detail, historical_context)
VALUES ('Hard times create strong men. Strong men create good times. Good times create weak men. And weak men create hard times.', 'G. Michael Hopf', '2016', 'https://ui-avatars.com/api/?name=G+Michael+Hopf&background=8B5E3C&color=fff&size=80&rounded=true', 'https://en.wikipedia.org/wiki/G._Michael_Hopf', 'Author and former Marine', 'Novel', 'Those Who Remain (2016), a post-apocalyptic novel', 'This cyclical theory of civilization — from a post-apocalyptic novel — went viral because it articulated something people felt intuitively about generational cycles and the paradox of prosperity.');

INSERT INTO quotes (text, speaker, date, thumbnail, social_link, role, source, source_detail, historical_context)
VALUES ('The world is a book, and those who do not travel read only one page.', 'St. Augustine', 'c. 400 AD', 'https://ui-avatars.com/api/?name=St+Augustine&background=D4956B&color=fff&size=80&rounded=true', 'https://en.wikipedia.org/wiki/Augustine_of_Hippo', 'Bishop of Hippo, theologian', 'Attributed', 'Widely attributed to Augustine', 'From a man who journeyed from North Africa to Rome to Milan — both physically and spiritually. A call to curiosity that predates the age of exploration by a thousand years.');

INSERT INTO quotes (text, speaker, date, thumbnail, social_link, role, source, source_detail, historical_context)
VALUES ('Freedom is never voluntarily given by the oppressor; it must be demanded by the oppressed.', 'Martin Luther King Jr.', 'April 16, 1963', 'https://ui-avatars.com/api/?name=Martin+Luther+King&background=C49A6C&color=fff&size=80&rounded=true', 'https://en.wikipedia.org/wiki/Martin_Luther_King_Jr.', 'Baptist minister, civil rights leader', 'Letter', 'Letter from Birmingham Jail', 'Written on scraps of newspaper in a jail cell, this letter answered white moderates who told King to ''wait.'' It remains the definitive case for the urgency of justice.');

INSERT INTO quotes (text, speaker, date, thumbnail, social_link, role, source, source_detail, historical_context)
VALUES ('The only way to do great work is to love what you do. If you haven''t found it yet, keep looking. Don''t settle.', 'Steve Jobs', 'June 12, 2005', 'https://ui-avatars.com/api/?name=Steve+Jobs&background=A67C5B&color=fff&size=80&rounded=true', 'https://en.wikipedia.org/wiki/Steve_Jobs', 'Co-founder and CEO of Apple', 'Speech', 'Stanford University Commencement Address', 'A year after his cancer diagnosis, Jobs told Stanford graduates three stories about death, love, and loss. The speech became the most-watched commencement address in history.');

INSERT INTO quotes (text, speaker, date, thumbnail, social_link, role, source, source_detail, historical_context)
VALUES ('The true soldier fights not because he hates what is in front of him, but because he loves what is behind him.', 'G.K. Chesterton', '1911', 'https://ui-avatars.com/api/?name=GK+Chesterton&background=CC785C&color=fff&size=80&rounded=true', 'https://en.wikipedia.org/wiki/G._K._Chesterton', 'English writer, philosopher, and Christian apologist', 'Newspaper column', 'Illustrated London News, January 14, 1911', 'Chesterton reframes the warrior''s motivation from destruction to protection. In a world that glorifies disruption, he reminds us that the deepest courage is conservational — fighting to preserve what you love.');

INSERT INTO quotes (text, speaker, date, thumbnail, social_link, role, source, source_detail, historical_context)
VALUES ('I have not failed. I''ve just found 10,000 ways that won''t work.', 'Thomas Edison', 'c. 1903', 'https://ui-avatars.com/api/?name=Thomas+Edison&background=8B5E3C&color=fff&size=80&rounded=true', 'https://en.wikipedia.org/wiki/Thomas_Edison', 'Inventor and businessman', 'Attributed', 'Various interviews and biographies', 'Edison''s reframe of failure as data turned relentless experimentation into a philosophy. In an era that celebrates overnight success, his actual path — thousands of failed experiments — is the truer model.');

INSERT INTO quotes (text, speaker, date, thumbnail, social_link, role, source, source_detail, historical_context)
VALUES ('To be yourself in a world that is constantly trying to make you something else is the greatest accomplishment.', 'Ralph Waldo Emerson', 'c. 1841', 'https://ui-avatars.com/api/?name=Ralph+Emerson&background=D4956B&color=fff&size=80&rounded=true', 'https://en.wikipedia.org/wiki/Ralph_Waldo_Emerson', 'American essayist, philosopher, and poet', 'Essay', 'Attributed, consistent with themes in "Self-Reliance" (1841)', 'Emerson''s transcendentalism was radical individualism before it was trendy. In an age of algorithmic conformity, his call to authentic selfhood is more countercultural than ever.');

