INSERT INTO quotes (text, speaker, date, thumbnail, social_link, role, source, source_detail, historical_context)
VALUES ('Man''s Search for Meaning taught me that suffering ceases to be suffering the moment it finds a meaning.', 'Viktor Frankl', '1946', 'https://ui-avatars.com/api/?name=Viktor+Frankl&background=C49A6C&color=fff&size=80&rounded=true', 'https://en.wikipedia.org/wiki/Viktor_Frankl', 'Austrian psychiatrist, Holocaust survivor, founder of logotherapy', 'Book', 'Man''s Search for Meaning (1946)', 'Written after surviving Auschwitz, Frankl''s discovery that meaning — not pleasure — sustains life through horror founded an entire school of psychotherapy and remains essential reading for the human condition.');

INSERT INTO quotes (text, speaker, date, thumbnail, social_link, role, source, source_detail, historical_context)
VALUES ('The only thing necessary for the triumph of evil is for good men to do nothing.', 'Edmund Burke', 'c. 1770', 'https://ui-avatars.com/api/?name=Edmund+Burke&background=A67C5B&color=fff&size=80&rounded=true', 'https://en.wikipedia.org/wiki/Edmund_Burke', 'Irish-British statesman, philosopher, and political theorist', 'Attributed', 'Widely attributed to Burke, though exact source is debated', 'The most quoted call to civic responsibility in the English language. Its persistence across centuries speaks to how easily good people rationalize inaction.');

INSERT INTO quotes (text, speaker, date, thumbnail, social_link, role, source, source_detail, historical_context)
VALUES ('If you want to build a ship, don''t drum up people to collect wood — teach them to long for the endless immensity of the sea.', 'Antoine de Saint-Exupery', '1948', 'https://ui-avatars.com/api/?name=Saint+Exupery&background=CC785C&color=fff&size=80&rounded=true', 'https://en.wikipedia.org/wiki/Antoine_de_Saint-Exup%C3%A9ry', 'French writer, poet, and aviator', 'Book', 'Citadelle (The Wisdom of the Sands), published posthumously in 1948', 'The author of The Little Prince understood that leadership is not about task management — it''s about vision casting. Every great movement begins not with logistics, but with longing.');

INSERT INTO quotes (text, speaker, date, thumbnail, social_link, role, source, source_detail, historical_context)
VALUES ('Two things are infinite: the universe and human stupidity; and I''m not sure about the universe.', 'Albert Einstein', 'c. 1940s', 'https://ui-avatars.com/api/?name=Albert+Einstein&background=8B5E3C&color=fff&size=80&rounded=true', 'https://en.wikipedia.org/wiki/Albert_Einstein', 'Theoretical physicist, Nobel laureate', 'Attributed', 'Widely attributed to Einstein in various forms', 'Even the mind that comprehended spacetime couldn''t comprehend human folly. Einstein''s dry wit here captures the eternal gap between our capacity for genius and our talent for self-destruction.');

INSERT INTO quotes (text, speaker, date, thumbnail, social_link, role, source, source_detail, historical_context)
VALUES ('What we do in life echoes in eternity.', 'Maximus Decimus Meridius', '2000', 'https://ui-avatars.com/api/?name=Maximus&background=D4956B&color=fff&size=80&rounded=true', 'https://en.wikipedia.org/wiki/Gladiator_(2000_film)', 'Fictional Roman general (Gladiator, portrayed by Russell Crowe)', 'Film', 'Gladiator (2000), directed by Ridley Scott', 'A fictional line that struck with the force of ancient truth. Sometimes Hollywood, reaching for myth, accidentally channels the real weight of the Stoic and Christian traditions it borrows from.');

INSERT INTO quotes (text, speaker, date, thumbnail, social_link, role, source, source_detail, historical_context)
VALUES ('Be the change you wish to see in the world.', 'Mahatma Gandhi', 'c. 1913', 'https://ui-avatars.com/api/?name=Mahatma+Gandhi&background=C49A6C&color=fff&size=80&rounded=true', 'https://en.wikipedia.org/wiki/Mahatma_Gandhi', 'Leader of Indian independence movement', 'Paraphrase', 'Paraphrase of Gandhi''s writing in Indian Opinion, 1913', 'The most famous paraphrase in history — Gandhi''s actual words were longer but the distilled version captured the essence: transformation starts with the self, not with demands on others.');

INSERT INTO quotes (text, speaker, date, thumbnail, social_link, role, source, source_detail, historical_context)
VALUES ('The best argument against democracy is a five-minute conversation with the average voter.', 'Winston Churchill', 'c. 1940s', 'https://ui-avatars.com/api/?name=Winston+Churchill&background=A67C5B&color=fff&size=80&rounded=true', 'https://en.wikipedia.org/wiki/Winston_Churchill', 'Prime Minister of the United Kingdom', 'Attributed', 'Widely attributed to Churchill', 'Churchill, democracy''s greatest wartime defender, was also its wittiest critic. The tension he names — that self-governance requires a quality of citizen that self-governance doesn''t guarantee — remains unresolved.');

