INSERT INTO quotes (text, speaker, date, thumbnail, social_link, role, source, source_detail, historical_context)
VALUES ('The measure of a man is what he does with power.', 'Plato', 'c. 380 BC', 'https://ui-avatars.com/api/?name=Plato&background=CC785C&color=fff&size=80&rounded=true', 'https://en.wikipedia.org/wiki/Plato', 'Athenian philosopher, founder of the Academy', 'Dialogue', 'Attributed, consistent with themes in The Republic', 'Plato saw power as the ultimate character test. In every era — from Athenian politics to Silicon Valley — the question remains: does power reveal who you are, or does it change who you are?');

INSERT INTO quotes (text, speaker, date, thumbnail, social_link, role, source, source_detail, historical_context)
VALUES ('I have a dream that my four little children will one day live in a nation where they will not be judged by the color of their skin but by the content of their character.', 'Martin Luther King Jr.', 'August 28, 1963', 'https://ui-avatars.com/api/?name=Martin+Luther+King&background=8B5E3C&color=fff&size=80&rounded=true', 'https://en.wikipedia.org/wiki/Martin_Luther_King_Jr.', 'Baptist minister, civil rights leader', 'Speech', '"I Have a Dream" speech at the March on Washington', 'Delivered to 250,000 people at the Lincoln Memorial, King''s dream of character over color became America''s aspirational north star — still invoked, still pursued, still unfinished.');

INSERT INTO quotes (text, speaker, date, thumbnail, social_link, role, source, source_detail, historical_context)
VALUES ('It is during our darkest moments that we must focus to see the light.', 'Aristotle', 'c. 340 BC', 'https://ui-avatars.com/api/?name=Aristotle&background=D4956B&color=fff&size=80&rounded=true', 'https://en.wikipedia.org/wiki/Aristotle', 'Greek philosopher, student of Plato, tutor to Alexander the Great', 'Attributed', 'Widely attributed to Aristotle', 'The father of logic and empiricism reminds us that perception narrows under stress. The discipline to look for light when surrounded by darkness is not optimism — it''s survival.');

INSERT INTO quotes (text, speaker, date, thumbnail, social_link, role, source, source_detail, historical_context)
VALUES ('The purpose of life is not to be happy. It is to be useful, to be honorable, to be compassionate, to have it make some difference that you have lived and lived well.', 'Ralph Waldo Emerson', 'c. 1844', 'https://ui-avatars.com/api/?name=Ralph+Emerson&background=C49A6C&color=fff&size=80&rounded=true', 'https://en.wikipedia.org/wiki/Ralph_Waldo_Emerson', 'American essayist, philosopher, and poet', 'Attributed', 'Widely attributed to Emerson, consistent with his philosophical themes', 'In a culture that equates happiness with purpose, Emerson offers a corrective: meaning comes from contribution, not consumption. The good life is the useful life.');

INSERT INTO quotes (text, speaker, date, thumbnail, social_link, role, source, source_detail, historical_context)
VALUES ('The cost of being wrong is less than the cost of doing nothing.', 'Seth Godin', '2010', 'https://ui-avatars.com/api/?name=Seth+Godin&background=A67C5B&color=fff&size=80&rounded=true', 'https://x.com/ThisIsSethsBlog', 'Author, entrepreneur, and marketing thought leader', 'Blog', 'Seth''s Blog — various posts on shipping and initiative', 'Godin distills the bias toward action that separates builders from bystanders. In an age of analysis paralysis, this is the nudge from the sideline into the arena.');

INSERT INTO quotes (text, speaker, date, thumbnail, social_link, role, source, source_detail, historical_context)
VALUES ('Courage is not the absence of fear, but the judgement that something else is more important than fear.', 'Ambrose Redmoon', '1972', 'https://ui-avatars.com/api/?name=Ambrose+Redmoon&background=CC785C&color=fff&size=80&rounded=true', 'https://en.wikipedia.org/wiki/James_Neil_Hollingworth', 'American writer and hippie community manager (born James Neil Hollingworth)', 'Essay', 'No Peaceful Warriors!, Gnosis Magazine', 'Often misattributed to FDR or Mandela, this redefinition of courage as prioritization rather than fearlessness resonates because it makes bravery accessible to everyone, not just the naturally bold.');

INSERT INTO quotes (text, speaker, date, thumbnail, social_link, role, source, source_detail, historical_context)
VALUES ('The only true wisdom is in knowing you know nothing.', 'Socrates', 'c. 399 BC', 'https://ui-avatars.com/api/?name=Socrates&background=8B5E3C&color=fff&size=80&rounded=true', 'https://en.wikipedia.org/wiki/Socrates', 'Athenian philosopher', 'Dialogue', 'Plato''s Apology — Socrates'' reflection on the Oracle of Delphi', 'The foundation of intellectual humility. In every era of confident certainty — including ours — Socrates'' radical admission of ignorance remains the starting point of real wisdom.');
